import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";

const MainStatusPage = () => {
  const [sensorData, setSensorData] = React.useState({
    water_level: "",
    gas: "",
    smoke: "",
    flame: "",
    shake: "",
    wind: "",
  });

  // const GetSensorData = async (e) =>{
  //   let response = fetch("https://ecourse.cpe.ku.ac.th/exceed07/api/update_sensor",{
  //     method:"GET"

  //   }

  // }

  const now = 8;
  const myToken = localStorage.getItem('myToken')
  
  React.useEffect(() => {
    setInterval(() => console.log(myToken), 1000);
  });

  const navigate = useNavigate();

  const onButtonClick = () => {
    navigate("/");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, user!</h2>
        <h2>Your home is currently safe!</h2>
      </div>
      <div className="main-page-sensor">
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Wind:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            variant="primary"
            type="submit"
            size="lg"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
