const returnPercentage = (e, type) => {
  if (type === "water_level") {
    let epc = (e / 1900) * 100;
    return epc.toFixed(2);
  }
  if (type === "gas") {
    if (e >= 2000) {
      return;
    } else {
      return 100;
    }
  }
  if (type === "smoke") {
    let epc = ((e - 1800) / 200) * 100;
    return epc;
  }
  if (type === "flame") {
    let epc = (Math.abs(e - 2700) / 2400) * 100;
    return epc;
  }
  if (type === "earthquake") {
    if (e === 0) {
      return 100;
    } else {
      return 0;
    }
  }
};

export default returnPercentage;
