import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";

const MainStatusPage = () => {
  const navigate = useNavigate();
  const myToken = localStorage.getItem("myToken");

  const [sensorData, setSensorData] = React.useState({
    water_level: 0,
    gas: 0,
    smoke: 0,
    flame: 0,
    shake: 0,
  });
  const [displayDetails, setDisplayDetails] = React.useState({
    water_level:{danger_level: "" ,display: ""},
    gas:{danger_level: "" ,display: ""},
    smoke:{danger_level: "" ,display: ""},
    flame:{danger_level: "" ,display: ""},
    shake:{danger_level: "" ,display: ""},
  })

  const dangerLevelmax ={// เป็น int
    water_level: 250,
    gas: 250,
    smoke: 250,
    flame: 250,
    shake: 250
  }
  const warningLevel ={ //เป็น %
    water_level: 50,
    gas: 50,
    smoke: 50,
    flame: 50,
    shake: 50
  }
  
  
  const now = 8;
  let myInterval;

  React.useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    myInterval = setInterval(async () => await UpdateSensor(myToken), 3000);
  },[]);

  const UpdateSensor = async (myToken) => {
    let response = await fetch(
      "https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/JSON",
          Authorization: `bearer ${myToken}`,
        },
      }
    );
    response = await response.json();
    setSensorData({
      water_level: response.result.water_level,
      gas: response.result.gas,
      smoke: response.result.smoke,
      flame: response.result.flame,
      shake: response.result.shake,
    });
    console.log(sensorData);
    //=========================================//
    let waterLevelpc = sensorData.water_level/dangerLevelmax.water_level*100
    let gaspc = sensorData.gas/dangerLevelmax.gas*100
    let smokepc = sensorData.smoke/dangerLevelmax.smoke*100
    let flamepc = sensorData.flame/dangerLevelmax.flame*100
    let shakepc = sensorData.shake/dangerLevelmax.shake*100
    //===========================================//
    if(100 > waterLevelpc > warningLevel.water_level){
      setDisplayDetails({...displayDetails, water_level:{warningLevel: 1,display: waterLevelpc}})
    }
    else if(waterLevelpc >= 100){
      setDisplayDetails({...displayDetails, water_level:{warningLevel: 2,display: waterLevelpc}})
    }
    else{
      setDisplayDetails({...displayDetails, water_level:{warningLevel: 0,display: waterLevelpc}})
    }
    //=============================//
    if(100 > sensorData.gas > warningLevel.gas){
      setDisplayDetails({...displayDetails, gas:{warningLevel: 1,display: gaspc}})
    }
    else if(sensorData.gas >= 100){
      setDisplayDetails({...displayDetails, gas:{warningLevel: 2,display: gaspc}})
    }
    else{
      setDisplayDetails({...displayDetails, water_level:{warningLevel: 0,display: gaspc}})
    }
    //===============================//
    if(100 > sensorData.smoke > warningLevel.smoke){
      setDisplayDetails({...displayDetails, smoke:{warningLevel: 1,display: smokepc}})
    }
    else if(sensorData.smoke >= 100){
      setDisplayDetails({...displayDetails, smoke:{warningLevel: 2,display: smokepc}})
    }
    else{
      setDisplayDetails({...displayDetails, smoke:{warningLevel: 0,display: smokepc}})
    }
    //==============================//
    if(100 > sensorData.flame > warningLevel.flame){
      setDisplayDetails({...displayDetails, flame:{warningLevel: 1,display: flamepc}})
    }
    else if(sensorData.flame >= 100){
      setDisplayDetails({...displayDetails, flame:{warningLevel: 2,display: flamepc}})
    }
    else{
      setDisplayDetails({...displayDetails, flame:{warningLevel: 0,display: flamepc}})
    }
    //===============================//
    if(100 > sensorData.shake > warningLevel.shake){
      setDisplayDetails({...displayDetails, shake:{warningLevel: 1,display: shakepc}})
    }
    else if(sensorData.shake >= 100){
      setDisplayDetails({...displayDetails, flame:{warningLevel: 2,display: flamepc}})
    }
    else{
      setDisplayDetails({...displayDetails, flame:{warningLevel: 0,display: flamepc}})
    }
  };


  const onButtonClick = () => {
    clearInterval(myInterval);
    localStorage.clear("myToken");
    localStorage.clear("currentUser");
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Wind:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            variant="primary"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
