import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";

const MainStatusPage = () => {
  const navigate = useNavigate();
  const myToken = localStorage.getItem("myToken");

  const [sensorData, setSensorData] = React.useState({
    water_level: 0,
    gas: 0,
    smoke: 0,
    flame: 0,
    shake: 0,
  });

  const checkProgressBar = (e) => {
    if (e >= 80){
      return "danger";
    }
    else if(e < 80 && e >= 40){
      return "warning";
    }
    else{
      return "success";
    }
  }

  const checkBackgroundColor = (e) => {
    if(e >= 80){
      return {backgroundColor: "#ED8975"}
    }
      else if(e < 80 && e >= 40){
        return {backgroundColor: "yellow"}
      }
      else{
        return {backgroundColor: "#37c291"}
      }

  }
  // const [displayDetails, setDisplayDetails] = React.useState({
  //   water_level: 10,
  //   gas: 10,
  //   smoke: 10,
  //   flame: 10,
  //   shake: 10,
  // });

