    //เป็น % เดี๋ยวคำนวณได้มั้งถ้า hardware มันให้ int มาอะ
    water_level: 40,
    gas: 40,
    smoke: 40,
    flame: 40,
    shake: 40,
  };

  let myInterval;
  React.useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    myInterval = setInterval(async () => await UpdateSensor(myToken), 3000);
  }, []);

  const UpdateSensor = async (myToken) => {
    let response = await fetch(
      "https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/JSON",
          Authorization: `bearer ${myToken}`,
        },
      }
    );
    response = await response.json();
    // setSensorData({
    //   water_level: (response.result.water_level)/dangerLevelmax.water_level*100,
    //   gas: (response.result.gas)/dangerLevelmax.gas*100,
    //   smoke: (response.result.smoke)/dangerLevelmax.smoke*100,
    //   flame: (response.result.flame)/dangerLevelmax.flame*100,
    //   shake: (response.result.shake)/dangerLevelmax.shake*100
    // });
    setSensorData({
      water_level: 100,
      gas: 60,
      smoke: 20,
      flame: 90,
      shake: 40
    });

    console.log(sensorData);

    checkProgressBar();
    //===========================================//

    // if (100 > waterLevelpc > warningLevel.water_level) {
    //   setDisplayDetails({ ...displayDetails, water_level: waterLevelpc });
    // } else if (waterLevelpc >= 100) {
    //   setDisplayDetails({ ...displayDetails, water_level: waterLevelpc });
    // } else {
    //   setDisplayDetails({ ...displayDetails, water_level: waterLevelpc });
    // }
    //=============================//
    // if(100 > gaspc > warningLevel.gas){
    //   setDisplayDetails({...displayDetails, gas:{warningLevel: 1,display: gaspc}})
    // }
    // else if(gaspc >= 100){
    //   setDisplayDetails({...displayDetails, gas:{warningLevel: 2,display: gaspc}})
    // }
    // else{
    //   setDisplayDetails({...displayDetails, water_level:{warningLevel: 0,display: gaspc}})
    // }
    //===============================//
    // if(100 > smokepc > warningLevel.smoke){
    //   setDisplayDetails({...displayDetails, smoke:{warningLevel: 1,display: smokepc}})
    // }
    // else if(smokepc >= 100){
    //   setDisplayDetails({...displayDetails, smoke:{warningLevel: 2,display: smokepc}})
    // }
    // else{
    //   setDisplayDetails({...displayDetails, smoke:{warningLevel: 0,display: smokepc}})
    // }
    //==============================//
    // if(100 > flamepc > warningLevel.flame){
    //   setDisplayDetails({...displayDetails, flame:{warningLevel: 1,display: flamepc}})
    // }
    // else if(flamepc >= 100){
    //   setDisplayDetails({...displayDetails, flame:{warningLevel: 2,display: flamepc}})
    // }
    // else{
    //   setDisplayDetails({...displayDetails, flame:{warningLevel: 0,display: flamepc}})
    // }
    //===============================//
    // if(100 > shakepc > warningLevel.shake){
    //   setDisplayDetails({...displayDetails, shake:{warningLevel: 1,display: shakepc}})
    // }
    // else if(shakepc >= 100){
    //   setDisplayDetails({...displayDetails, shake:{warningLevel: 2,display: shakepc}})
    // }
    // else{
    //   setDisplayDetails({...displayDetails, shake:{warningLevel: 0,display: shakepc}})
    // }

    // console.log(displayDetails);
  };

  const onButtonClick = () => {
    clearInterval();
    localStorage.clear("myToken");
    localStorage.clear("currentUser");
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div className="sensor-box" id="sensor-box-water" style={checkBackgroundColor(sensorData.water_level)}>
          <ProgressBar
            variant={checkProgressBar(sensorData.water_level)}
            now={sensorData.water_level}
            label={`${sensorData.water_level}%`}
          />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box" id="sensor-box-gas">
          <ProgressBar
            variant={checkProgressBar(sensorData.water_level)}
            now={sensorData.gas}
            label={`${sensorData.gas}%`}
          />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box" id="sensor-box-smoke">
          <ProgressBar
            variant={checkProgressBar(sensorData.smoke)}
            now={sensorData.smoke}
            label={`${sensorData.smoke}%`}
          />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box" id="sensor-box-flame">
          <ProgressBar
            variant={checkProgressBar(sensorData.flame)}
            now={sensorData.flame}
            label={`${sensorData.flame}%`}
          />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box" id="sensor-box-shake">
          <ProgressBar
            variant={checkProgressBar(sensorData.shake)}
            now={sensorData.shake}
            label={`${sensorData.shake}%`}
          />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            variant="primary"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
