import React from 'react'
import "./MainStatusPage.css"
import { useNavigate } from 'react-router-dom'
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from 'react-bootstrap';

const MainStatusPage = () => {
  const [sensorData,setSensorData] = React.useState({
    water_level:"",
    gas:"",
    smoke:"",
    flame:"",
    shake:"",
    wind:""
  })

  // const GetSensorData = async (e) =>{
  //   let response = fetch("https://ecourse.cpe.ku.ac.th/exceed07/api/update_sensor",{
  //     method:"GET"

  //   }

  // }


  const now = 110;


  React.useEffect(() => {
    setInterval(() => console.log("Hi"), 1000);
  });


  const navigate = useNavigate()

  const onButtonClick = () => {
    navigate("/")
  }

  return (
    <div className="main-page">
        <div class="welcome-box">
          <h2>Welcome back, user!</h2>
          <h2>Your home is currently safe!</h2>
        </div>
        <div className="main-page-sensor">
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
          <div className="sensor-box">
            <ProgressBar variant="success"now={now} label={`${now}%`} />
          </div>
        </div>
        <div className="logout-button">
          <Button variant="primary" type="submit" size="lg" onClick={onButtonClick}>
            Logout
          </Button>
        </div>
    </div>
  )
}

export default MainStatusPage
