import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";

const MainStatusPage = () => {
  const navigate = useNavigate();
  const myToken = localStorage.getItem("myToken");

  let interval_id = window.setInterval(() => {}, 99999);
  for (let i = 0; i < interval_id; i++) window.clearInterval(i);

  const [sensorData, setSensorData] = React.useState({
    water_level: 0,
    gas: 0,
    smoke: 0,
    flame: 0,
    shake: 0,
  });

  const checkProgressBar = (e) => {
    if (e >= 80) {
      return "danger";
    } else if (e < 80 && e >= 40) {
      return "warning";
    } else {
      return "success";
    }
  };

  const checkBackgroundColor = (e) => {
    if (e >= 80) {
      return { backgroundColor: "#8F0031" };
    } else if (e < 80 && e >= 40) {
      return { backgroundColor: "#aa9208" };
    } else if (e < 40 && e > 0) {
      return { backgroundColor: "#1b6148" };
    } else {
      return { backgroundColor: "#eeeeee" };
    }
  };
  // const [displayDetails, setDisplayDetails] = React.useState({
  //   water_level: 10,
  //   gas: 10,
  //   smoke: 10,
  //   flame: 10,
  //   shake: 10,
  // });

  const dangerLevelmax = {
    // เป็น int
    water_level: 250,
    gas: 250,
    smoke: 250,
    flame: 250,
    shake: 250,
  };
  const warningLevel = {
    //เป็น % เดี๋ยวคำนวณได้มั้งถ้า hardware มันให้ int มาอะ
    water_level: 40,
    gas: 40,
    smoke: 40,
    flame: 40,
    shake: 40,
  };

  let myInterval;
  React.useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    myInterval = setInterval(async () => await UpdateSensor(myToken), 3000);
  }, []);

  const UpdateSensor = async (myToken) => {
    let response = await fetch(
      "https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/JSON",
          Authorization: `bearer ${myToken}`,
        },
      }
    );
    response = await response.json();
    // setSensorData({
    //   water_level: (response.result.water_level)/dangerLevelmax.water_level*100,
    //   gas: (response.result.gas)/dangerLevelmax.gas*100,
    //   smoke: (response.result.smoke)/dangerLevelmax.smoke*100,
    //   flame: (response.result.flame)/dangerLevelmax.flame*100,
    //   shake: (response.result.shake)/dangerLevelmax.shake*100
    // });
    setSensorData({
      water_level: 100,
      gas: 60,
      smoke: 20,
      flame: 90,
      shake: 40,
    });

    console.log(sensorData);

    checkProgressBar();
  };

  const onButtonClick = () => {
    let interval_id = window.setInterval(() => {}, 99999);
    for (let i = 0; i < interval_id; i++) window.clearInterval(i);
    localStorage.clear("myToken");
    localStorage.clear("currentUser");
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div
          className="sensor-box"
          id="sensor-box-water"
          style={checkBackgroundColor(sensorData.water_level)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.water_level)}
            now={sensorData.water_level}
            label={`${sensorData.water_level}%`}
          />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-gas"
          style={checkBackgroundColor(sensorData.gas)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.gas)}
            now={sensorData.gas}
            label={`${sensorData.gas}%`}
          />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-smoke"
          style={checkBackgroundColor(sensorData.smoke)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.smoke)}
            now={sensorData.smoke}
            label={`${sensorData.smoke}%`}
          />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-flame"
          style={checkBackgroundColor(sensorData.flame)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.flame)}
            now={sensorData.flame}
            label={`${sensorData.flame}%`}
          />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-shake"
          style={checkBackgroundColor(sensorData.shake)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.shake)}
            now={sensorData.shake}
            label={`${sensorData.shake}%`}
          />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
