const checkProgressBar = (e, type) => {
    if(type === "water_level"){
        let epc = e/1900*100
        if(epc >= 100){
            return "danger"
        }
        else if(epc < 100 && epc > 84.21){
            return "warning"
        }
        else{
            return "success"
        }
    }
    if(type === "gas"){
        if(e >= 2000){
            return "success"
        }
        else{
            return "danger"
        }
    }
    if(type === "smoke"){
        let epc = (e-1800)/200*100
        if(epc >= 100){
            return "danger"
        }
        
        else if(epc < 100 && epc >= 50){
            return "warning"
        }
        else{
            return "success"
        }
    }
    if(type === "flame"){
        let epc = (Math.abs(e-2700))/2400*100
        if(epc >= 100){
            return "danger"
        }
        else if(epc < 100 && epc >= 91.66){
            return 
        }
        
    }
    if(type === "shake"){
        let epc = e/1900*100
    }
  
}

export default checkProgressBar
