    if(type === "water_level"){
        let epc = e/1900*100
        if(epc > 100){
            return "danger"
        }
        else if(epc <= 100 && epc > 84.21){
            return "warning"
        }
        else{
            return "success"
        }
    }
    if(type === "gas"){
        if(e >= 2000){
            return "safe"
        }
        else{
            return "danger"
        }
    }
    if(type === "smoke"){
        let epc = 2000-1800
    }
    if(type === "flame"){
        let epc = e/1900*100
    }
    if(type === "shake"){
        let epc = e/1900*100
    }
  
}

