const checkDangerLevel = (e,type,ret) => {
  if(type === "water_level"){
      if(e >= 1900){
          if(ret === "prog"){
              return "danger"
          }
          else if(ret === "back"){
              return { backgroundColor: "#8F0031" };
          }
          else if(ret === "text"){
              return "SEVERE"
          }
      }
      else if(e < 1900 && e >= 1600){
          if(ret === "prog"){
              return "warning"
          }
          else if(ret === "back"){
              return { backgroundColor: "#FDA50F" };
          }
          else if(ret === "text"){
              return "CONCERNING"
          }
      }
      else{
          if(ret === "prog"){
              return "success"
          }
          else if(ret === "back"){
              return { backgroundColor: "#1b6148" };
          }
          else if(ret === "text"){
              return "SAFE"
          }
      }
  }
  else if(type === "shake"){
      if(e === 0){
          if(ret === "prog"){
              return "danger"
          }
          else if(ret === "back"){
              return { backgroundColor: "#8F0031" };
          }
          else if(ret === "text"){
              return "POSSIBLE EARTHQUAKE HAS BEEN DETECTED!"
          }
      }
      else if(e === 1){
          if(ret === "prog"){
              return "success"
          }
          else if(ret ==="back"){
              return { backgroundColor: "#FDA50F" };
          }
          else if(ret === "text"){
              return "Earthquake is not detected currently"
          }
      }
  }
  else if(type ==="flame"){
      if(e <= 300){
          if(ret === "prog"){
              return "danger"
          }
          else if(ret ==="back"){
              return { backgroundColor: "#8F0031" };
          }
          else if(ret ==="text"){
              return "POSSIBLE FIRE HAZARD HAS BEEN DETECTED!"
          }
      }
      else if(e >300 && e <= 500){
          if(ret === "prog"){
              return "warning"
          }
      }
  }
}

export default checkDangerLevel
