const returnPercentage = (e, type) => {
    if(type === "water_level"){
        let epc = e/1900*100
        return epc
    }
    if(type === "gas"){
        if(e >= 2000){
        return 0
        }
        else{
            return 100
        }
    }
    if(type === "smoke"){
        let epc = (e-1800)/200*100
        return epc
    }  
    if(type === "flame"){
        let epc = (Math.abs(e-2700))/2400*100
        return epc
    }
}

export default returnPercentage
