import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";
import axios from 'axios'

const accessToken = localStorage.getItem('myToken')
axios.interceptors.request.use(
  config => {
    config.headers.authorization = `Bearer ${accessToken}`
    return config
  }
)
const endPoint = "https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor"

const MainStatusPage = () => {
  const [sensorData, setSensorData] = React.useState({
    water_level: "0",
    gas: "0",
    smoke: "0",
    flame: "0",
    shake: "0",
    wind: "0",
  });

  const now = 8;
  let myInterval;
  React.useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    myInterval = setInterval(() => UpdateSensor(myToken), 1000);
  });
  
  const UpdateSensor = async (myToken) => {
    // let response = await fetch("https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor",{
    //   method: 'GET',
    //   headers:{
    //       "Content-Type": "application/JSON",
    //       "Authorization": `bearer ${myToken}`
    //   }
    // })
    // response = await response.json()
    // console.log(response)
  }

  const navigate = useNavigate();

  const onButtonClick = () => {
    clearInterval(myInterval)
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Wind:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            variant="primary"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
