    })
    return response;
  };

  const onButtonClick = () => {
    clearInterval(myInterval);
    localStorage.clear("myToken");
    localStorage.clear("currentUser");
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Water Level:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Gas:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Smoke:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Flame:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Shake:</p>
          <p>Danger Level:</p>
        </div>
        <div className="sensor-box">
          <ProgressBar variant="success" now={now} label={`${now}%`} />
          <p>Wind:</p>
          <p>Danger Level:</p>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            variant="primary"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
