import React from "react";
import "./LoginForm.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { Redirect } from "react-router-dom";

const LoginForm = () => {
  const [details, setDetails] = React.useState({ username: "", password: "" });

  const handleSubmitClick = async (e) => {
    e.preventDefault();

    console.log("Clicked");
    const hashedpassword = await sha256(details.password)
    console.log({...details, password: hashedpassword})
    

  };
  async function sha256(message) {
    const msgBuffer = new TextEncoder().encode(message);                    
    const hashBuffer = await crypto.subtle.digest('SHA-256', msgBuffer);
    const hashArray = Array.from(new Uint8Array(hashBuffer));             
    const hashHex = hashArray.map(b => b.toString(16).padStart(2, '0')).join('');
    return hashHex;
  }

  return (
    <div className="login-box">
      <Form>
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label class="login-page-label">Email address</Form.Label>
          <Form.Control onChange ={e => setDetails({...details, username: e.target.value})} value = {details.username} type="email" placeholder="Enter email" />
          <Form.Text className="text-muted"></Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicPassword">
          <Form.Label class="login-page-label">Password</Form.Label>
          <Form.Control onChange ={e => setDetails({...details, password: e.target.value})} value = {details.password} type="password" placeholder="Password" />
        </Form.Group>

        <Button
          id="login-page-submit"
          variant="primary"
          type="submit"
          onClick={handleSubmitClick}
        >
          Submit
        </Button>
      </Form>
      <div class="login-page-register-part">
        <label>Don't have an account?</label>
        <a href="/register"> Register</a>
      </div>
    </div>
  );
};

export default LoginForm;
