import React from "react";
import "./MainStatusPage.css";
import { useNavigate } from "react-router-dom";
import Button from "react-bootstrap/Button";
import "bootstrap/dist/css/bootstrap.min.css";
import { ProgressBar } from "react-bootstrap";
import returnPercentage from "./functions/returnPercentage";
import checkDangerLevel from "./functions/checkDangerLevel";

const MainStatusPage = () => {
  const navigate = useNavigate();
  const myToken = localStorage.getItem("myToken");

  const [sensorData, setSensorData] = React.useState({
    water_level: 0,
    gas: 0,
    smoke: 0,
    flame: 0,
    shake: 0,
  });

  // const [displayDetails, setDisplayDetails] = React.useState({
  //   water_level: 10,
  //   gas: 10,
  //   smoke: 10,
  //   flame: 10,
  //   shake: 10,
  // });


  const checkProgressBar = (e) => {
    if (e >= 80) {
      return "danger";
    } else if (e < 80 && e >= 40) {
      return "warning";
    } else {
      return "success";
    }
  };

  const checkBackgroundColor = (e) => {
    if (e >= 80) {
      return { backgroundColor: "#8F0031" };
    } else if (e < 80 && e >= 40) {
      return { backgroundColor: "#FDA50F" };
    } else if (e < 40 && e > 0) {
      return { backgroundColor: "#1b6148" };
    } else {
      return { backgroundColor: "#eeeeee" };
    }
  };

  // eslint-disable-next-line no-unused-vars
  let myInterval;
  React.useEffect(() => {
    // eslint-disable-next-line react-hooks/exhaustive-deps
    myInterval = setInterval(async () => await UpdateSensor(myToken), 3000);
  }, []);

  const UpdateSensor = async (myToken) => {
    let response = await fetch(
      "https://ecourse.cpe.ku.ac.th/exceed07/api/get_sensor",
      {
        method: "GET",
        headers: {
          "Content-Type": "application/JSON",
          Authorization: `bearer ${myToken}`,
        },
      }
    );

    response = await response.json();
    // setSensorData({
    //   water_level: response.result.water_level
    //   gas: response.result.gas
    //   smoke: response.result.smoke
    //   flame: response.result.flame
    //   shake: response.result.shake
    
    setSensorData({
      water_level: 800,
      gas: 2020,
      smoke: 1850,
      flame: 2200,
      shake: 1,
    });
    //test

    console.log(sensorData);
  };

  const onButtonClick = () => {
    let interval_id = window.setInterval(() => {}, 99999);
    for (let i = 0; i < interval_id; i++) window.clearInterval(i);
    localStorage.clear("myToken");
    localStorage.clear("currentUser");
    navigate("/backtologin");
  };

  return (
    <div className="main-page">
      <div class="welcome-box">
        <h2>Welcome back, {localStorage.getItem("currentUser")}!</h2>
      </div>
      <div className="main-page-sensor">
        <div
          className="sensor-box"
          id="sensor-box-water"
          style={checkDangerLevel(sensorData.water_level,"water_level","back")}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.water_level)}
            now={returnPercentage(sensorData.water_level,"water_level")}
            label={`${returnPercentage(sensorData.water_level,"water_level")}%`}
          />
          <div className="sensor-box-text">
            <p>Water Level:</p>
            <p>Danger Level:</p>
          </div>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-gas"
          style={checkBackgroundColor(sensorData.gas)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.gas)}
            now={returnPercentage(sensorData.gas,"gas")}
            label={`${returnPercentage(sensorData.gas,"gas")}%`}
          />
          <div className="sensor-box-text">
          <p>Gas:</p>
          <p>Danger Level:</p>
          </div>
          <div className="sensor-box-icon">
          </div>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-smoke"
          style={checkBackgroundColor(sensorData.smoke)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.smoke)}
            now={returnPercentage(sensorData.smoke,"smoke")}
            label={`${returnPercentage(sensorData.smoke,"smoke")}%`}
          />
          <div className="sensor-box-text">
          <p>Smoke:</p>
          <p>Danger Level:</p>
          </div>
          <div className="sensor-box-icon"></div>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-flame"
          style={checkBackgroundColor(sensorData.flame)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.flame)}
            now={returnPercentage(sensorData.flame,"flame")}
            label={`${returnPercentage(sensorData.flame,"flame")}%`}
          />
          <div className="sensor-box-text">
          <p>Flame:</p>
          <p>Danger Level:</p>
          </div>
          <div className="sensor-box-icon"></div>
        </div>
        <div
          className="sensor-box"
          id="sensor-box-shake"
          style={checkBackgroundColor(sensorData.shake)}
        >
          <ProgressBar
            variant={checkProgressBar(sensorData.shake)}
            now={returnPercentage(sensorData.shake,"shake")}
            label={`${returnPercentage(sensorData.shake,"shake")}%`}
          />
          <div className="sensor-box-text">
          <p>Shake:</p>
          <p>Danger Level:</p>
          </div>
          <div className="sensor-box-icon"></div>
        </div>

        <div className="logout-button-box">
          <Button
            id="logout-button"
            type="submit"
            size="lg"
            variant="danger"
            onClick={onButtonClick}
          >
            Logout
          </Button>
        </div>
      </div>
    </div>
  );
};

export default MainStatusPage;
